import './Dashboard.css'
import Schedule from './Components/Schedule'
import Persona from './Components/Persona'
import Calendly from './Components/Calendly';
import Ref from './Components/Ref'
import React, {useState} from 'react'
import s from '../images/s.png'
import firebase from 'firebase';

function Dashboard(props) {
  const [calendar, setCalendar] = useState("")
  const [who, setWho] = useState("")
  const [web, setWeb] = useState("")
  const [open, setOpen] = useState("is-active")

  function changeWho(event){
    event.preventDefault()
    setWho(event.target.value)
  }

function changeWeb(event){
  event.preventDefault()
  setWeb(event.target.value)
}

function changeCalendar(event){
  event.preventDefault()
  setCalendar(event.target.value)
}

function handleSubmit(event){
  // window.alert(event)
  event.preventDefault()
  console.log(who)
  fetch('/ref/schedule', {
    method: 'post',
    headers: {'Content-Type':'application/json'},
    body: JSON.stringify({
         "email": email, by:"customer interview"
    }),})
    .then(response => response.json())
    .then(data => {
    const success = data.success
    console.log(success)
    //checking if user already exists and if our api call was successful
    if (success == "true"){
        if( data.goto == "dashboard"){
            history.push("/", {id:data.id, counts:data.counts});
        } else{
            history.push("/", {id:data.id, counts:data.counts});
        }
    }
})
.catch((error) => {
    console.error('Error: Fetch');
})
}

function handleSchedule(event){
  event.preventDefault()
  if (open == "is-active") {
    setOpen("is-not active")
  } else{
    setOpen("is-active")
  }
}



    return (
        <div className="container-Dashboard">
                        <h1 className="Rowdies header"> Customer <span className="orange-header">Interviews </span></h1>
                        <h4 className="Rowdies "> Welcome {props.name} </h4>
                        <h4 className="Rowdies "> Your email is {props.email} </h4>
            <div className="columns is-mobile">
                <div className="column is-half is-offset-one-quarter ">
                <div className="container-schedule">
            <br></br>
            <p className="s-font-header white">Schedule your first customer interview!</p>
            <p className="s-font-main white">Letâ€™s get you the first customer interviews! Get product feedback, chat with potential customers, or just get some insights into a space</p>
            <br></br>
            <button className="s-button" onClick={handleSchedule}> ðŸ“†Schedule </button>
            <img src={s} className="s-img"></img>
        </div>
                  <br></br>
                  <Persona></Persona>
                  <br></br>
                  <Calendly> </Calendly>
                  <br></br>
                  <Ref></Ref>
                  <br></br>
                  <div className={`modal ${open}`} >
                  <div className="modal-background"></div>
                    <div className="modal-card">
                      <header className="modal-card-head">
                        <p className="modal-card-title">Interview Request </p>
                        <button class="delete" aria-label="close"></button>
                      </header>
                      <section className="modal-card-body">
                      <form type="submit">
                      <p>To schedule an interview please use the form below</p>
                      <br></br>
                      <p>Calender Link</p>
                      <input value={calendar} onChange={changeCalendar} className="input-lg"></input>
                      <br></br>
                      <p>Your website URL</p>
                      <input value={web} onChange={changeWeb}  className="input-lg"></input>
                      <br></br>
                      <p>Who are you looking to interview </p>
                      <input value={who} onChange={changeWho}  className="input-lg"></input>


                      <footer className="modal-card-foot">
                        <input className="button is-success" type="submit" onClick={handleSubmit}></input>
                        <button className="button" onClick={handleSchedule}>Cancel</button>
                      </footer>
                      </form>
                      </section>
                    </div>
                  </div>
                </div>

            </div>

        </div>

    );
  }

  export default Dashboard;
